const acme = require('acme-client');
const fs = require('fs');

async function gen() {


    /* Init client */
    const client = new acme.Client({
        directoryUrl: acme.directory.letsencrypt.%commitment%,
        accountKey: await acme.forge.createPrivateKey()
    });

    /* Create CSR */
    const [key, csr] = await acme.forge.createCsr({
        commonName: '%hostName%'
    });

    /* Certificate */
    const cert = await client.auto({
        csr,
        email: '%email%',
        termsOfServiceAgreed: true,
        challengeCreateFn,
        challengeRemoveFn
    });

    /* Done */
    if("%commitment%" === "production"){
        fs.writeFileSync('/home/%systemUser%/.ssl/%hostName%.csr', csr.toString());
        fs.writeFileSync('/home/%systemUser%/.ssl/%hostName%.key' , key.toString());
        fs.writeFileSync('/home/%systemUser%/.ssl/%hostName%.cert' , cert.toString());
        log("Production exercise complete. Three files written to /home/%systemUser%/.ssl/")
    }else{
        log("Staging exercise complete. No files written to /home/%systemUser%/.ssl/")
    }
};
gen();

