provides runtime function:
function %functionName%(divHtmlToUpdate)
this calls rowClickFunctionName to set the onClick event code

tempArr.c:
var allAnswers = [];
function %functionName%(divHtmlToUpdate, questionField, answerField){
    var mydata={'id':'#'};
    $.ajax({
        type: 'get',
        data: mydata,
        dataType : "text",
        headers: {
            "content-type": "application/json"
        },
        url: '/%dataStoreGetService%',
        success : function(result) {

        var ResultsJson = JSON.parse(result);

        var x = 0;
        ResultsJson.forEach(function(row){
            var html = '';
        	var question = row.%questionField%;
        	question = question.replace(/</g, "&lt;").replace(/>/g, "&gt;");

        	var answer= row.%answerField%;

    		html += '<p class="%questionField%">'+%questionField%+'</p>';
            html += '<div id=faqtem'+x.toString() + ' class="%answerField%"></div>';

            $("#"+divHtmlToUpdate).append(html) ;

            var quillObj = new Quill("#faqtem"+x.toString(), {
                theme: 'snow',
                modules: {
                    "toolbar": false
                }
            })

            quillObj.enable(false);

            if(answer.hasOwnProperty("ops")){
                quillObj.setContents(answer.ops)
            }else{
                quillObj.setContents(
                    [{ insert:  answer },
                    { insert: '\n' }]
                );
            };

            x++     
        });

        },
        error : function(error) {
            console.log(error)
        }
    })
}

