function %functionName%(idA, idB, arrowId) {
    if(idA !== "" && idB !== "" && arrowId !== ""){
        var divA       = document.querySelector("#"+idA);
        var divB       = document.querySelector("#"+idB);
        
        var arrow = document.querySelector("#" + arrowId);
        
        if(divA && divB && arrow){
            var bBoxA = divA.getBBox();
            var bBoxB = divB.getBBox();
        
            var scrollPos = $(".scroller").scrollTop();
            var aTop = $(divA).offset().top + bBoxA.height/2;
            
            var posnA = {
                x: getOffset(divA).left + bBoxA.width + 10,
                y: aTop + scrollPos 
            };
            
            var bTop = $(divB).offset().top + bBoxB.height/2;
        
            var posnB = {
                x: getOffset(divB).left - 14,
                y: bTop + scrollPos
            };
            
            // if($(window).width() > 450){
            //     posnA.x -= 30;
            //     posnB.x -= 30;
                
            // }
        
            var xDist = Math.max(Math.abs(posnB.x - posnA.x)/3,100);
            var yDist = posnB.y - posnA.y;
            var dStr =
                "M" +
                (posnA.x) + "," + (posnA.y) + " " +
                "C" +
                (posnA.x + xDist ) + "," + (posnA.y) + " " +
                (posnB.x - xDist) + "," + (posnB.y) + " " +
                (posnB.x) + "," + ( posnB.y);
                
            arrow.setAttribute("d", dStr);
        }
    }
};

function getOffset(el) {
  const rect = el.getBoundingClientRect();
  return {
    left: rect.left + window.scrollX,
    top: rect.top + window.scrollY
  };
}

