function %functionName%(name){
        const styleName = name;

        const cssPath = '%cssPath%' + name + '.css';
        const mydata = {};
        $.ajax({
                    type: 'get',
                    data: mydata,
                    dataType : "text",
                    headers: {
                        "content-type": "application/json"
                    },
                    url: cssPath,
                    success : function(data) {
        var sheet;
        if (lastStyleSheet){
            sheet = lastStyleSheet;
        }else{
            sheet = document.createElement('style');
            document.body.appendChild(sheet);
        }
        lastStyleSheet = sheet;

        cssUnsortTxt = data;
        var cssArr= cssUnsortTxt.split("}")
        shuffleArray(cssArr);
        cssTxt = cssArr.join("}");

        loadCssParts( 0, lastStyleSheet);

        if(name === "dark"){
            // %insertSetDarkStyleJs%
        }else{
            // %insertSetDefaultStyleJs%
        }



                    },
                    error : function(error) {
                        console.log(error)
                    }
                })



        var sheet;


    }
    var lastStyleSheet = "";

    function shuffleArray(array) {
        for (let i = array.length - 1; i > 0; i--) {
            const j = Math.floor(Math.random() * (i + 1));
            [array[i], array[j]] = [array[j], array[i]];
        }
    }

    var cssTxt="";
    var cssUnsortTxt="";
    function loadCssParts( part, sheet){

        var cssTxtPart = cssTxt.split("}")[part];
        var totRows = cssTxt.split("}").length;
        if( totRows > part){
            sheet.innerHTML += cssTxtPart + "}";
            setTimeout('loadCssParts('+(part+1).toString()+', lastStyleSheet)', 1500/totRows);
        }else{
            sheet.innerHTML = "";
            sheet.innerHTML = cssUnsortTxt
        }
    }

