const net = require('net');
const { URL } = require('url');
const httpProxy = require("http-proxy");

var thisHttpProxy = httpProxy.createProxyServer({});
thisHttpProxy.on("error", function (err, req, res) {
    // console.log("http proxy error", err);
    res.end();
});

// Create an HTTP tunneling proxy
const proxy = http.createServer((req, res) => {
    //Proxy http requests
    //console.log("http request", req.url);

    var urlObj = new URL(`${req.url}`);  
    var target = urlObj.protocol + "//" + urlObj.host;

    //console.log("Proxy HTTP request for:", target);
    thisHttpProxy.web(req, res, {target: target});
});

proxy.on('connect', (req, clientSocket, head) => {
    //Proxy https requests
    const { port, hostname } = new URL(`http://${req.url}`);
    //console.log("https request", req.url);
    if (hostname && port) {

        const serverErrorHandler = (err) => {
            //console.log(err.message);
            if (clientSocket) {
                clientSocket.end(`HTTP/1.1 500 ${err.message}\r\n`);
            }
        };

        const serverEndHandler = () => {
            // console.log("serverEndHandler");
            if (clientSocket) {
                // console.log("clientSocket ending");
                clientSocket.end(`HTTP/1.1 500 External Server End\r\n`);
            }
        };

        const clientErrorHandler = (err) => {
            // console.log(err.message);
            if (serverSocket) {
                serverSocket.end();
            }
        };

        const clientEndHandler = () => {
        //console.log("clientEndHandler")
            if (serverSocket) {
                serverSocket.end();
            }
        };

        clientSocket.on('error', clientErrorHandler);
        clientSocket.on('end', clientEndHandler);

        const serverSocket = net.connect(port, hostname, () => {

            clientSocket.write('HTTP/1.1 200 Connection Established\r\n' +
                'Proxy-agent: Node.js-Proxy\r\n' +
                '\r\n');

            serverSocket.write(head);

            serverSocket.pipe(clientSocket);
            clientSocket.pipe(serverSocket);
        });

        serverSocket.on('error', serverErrorHandler);
        serverSocket.on('end', serverEndHandler);

    } else {
        //console.log("HTTP/1.1 400 Bad Request");
        clientSocket.end('HTTP/1.1 400 Bad Request\r\n');
        clientSocket.destroy();
    }
});

