    var viewId = '';
    var lastUserList = "";

    var lastGetSessionId = '';
    var getIndex = 0;
    var lastIndex = -1;
    var timestampOffset = 0;

    function setMediaSource(){
        lastGetSessionId = '';
        getIndex = 0;
        lastIndex = -1;
        timestampOffset = 0;

        cacheBlobs = [];
        arrayOfBlobs = [];

        mediaSource = new MediaSource();
        var url = URL.createObjectURL(mediaSource); 
        play_audio.src = url;

        mediaSource.addEventListener("sourceopen", function(){
            console.log("mediaSource  sourceopen");
            sourceBuffer = mediaSource.addSourceBuffer('audio/webm; codecs="opus"'); //video/webm; codecs="opus,vp8
            sourceBuffer.mode = 'sequence';
            sourceBuffer.addEventListener("updateend", appendToSourceBuffer);
        });
        mediaSource.addEventListener("sourceended", function(){
            console.log("**************mediaSource  sourceended*************");
            setMediaSource()
        });
        mediaSource.addEventListener("sourceclose", function(){
            console.log("mediaSource  sourceclose")
        })

    }
    setMediaSource();

    var view_canvas = document.getElementById('view_canvas');
    view_canvas.width = width;
