    webStreamCanvasImageContainer = new Image();
    webStreamCanvasImageContainer.height = height; webStreamCanvasImageContainer.width = width; 

    webStreamCanvasImageContainer.onload = function(e) {
        ctx2.drawImage(webStreamCanvasImageContainer,0, 0, width, height);
    };   

    setTimeout( getMedia  ,currentstartSliceSetting);
    function getMedia(){

        if(viewId === ''){
            setTimeout( getMedia  ,currentstartSliceSetting)
            return;
        }

        const recData = {'userId':viewId, 'index': getIndex, 'postSessionId':lastGetSessionId };
        //console.log("requesting", getIndex);

         $.get('/mediaGet', recData, function(data) {
            const dataObj = data;

            var gotIndex =  parseInt(dataObj.index, 10);
            const getSessionId = dataObj.postSessionId;

            if(getSessionId !== lastGetSessionId){
                console.log('getSessionId !== lastGetSessionId', mediaSource.readyState);
                lastIndex = -1;
                lastGetSessionId = getSessionId;
                cacheBlobs = [];
                arrayOfBlobs = [];

                //incase buffer is swollen
                // if(mediaSource.readyState === "open" && sourceBuffer){
                //     if(sourceBuffer.buffered.length){
                //         timestampOffset = sourceBuffer.buffered.end(
                //             sourceBuffer.buffered.length - 1
                //         )
                //     }    
                // }
            }

            const mediaLength =  parseInt(dataObj.mediaLength, 10);
            if(!dataObj.hasOwnProperty("media")){
                    //console.log({'fail index':gotIndex, 'lastIndex':lastIndex, 'mediaLength':mediaLength});
            }else{ 
                const frameData = dataObj.frameData;
                const mediaChunkData = dataObj.media.mediaChunk;
                const mediaChunkDataLen = dataObj.media.mediaChunk.length;

                getIndex = mediaLength;

                webStreamCanvasImageContainer.src = frameData;

                //console.log({'get index':getIndex,'got index':gotIndex, 'lastIndex':lastIndex, 'mediaChunkDataLen':mediaChunkDataLen, 'mediaLength': mediaLength });
                if (gotIndex !== lastIndex+1){
                    if(cacheBlobs.length > 10){

                    }else if(gotIndex > lastIndex){
                        console.log("storing - " + gotIndex);
                        cacheBlobs.push(dataObj)
                    }else{
                        //dump
                    }
                }else{
                    pushChunks(mediaChunkData, gotIndex);
                    lastIndex = gotIndex + mediaChunkDataLen -1 ;
                    // console.log("next - " + index)

                    if(cacheBlobs.length > 0){
                        if(cacheBlobs.length > 1){
                            cacheBlobs.sort(function(a, b) {
                                var idxA = parseInt(a.index, 10); 
                                var idxB = parseInt(b.index, 10); 
                                return idxA - idxB;
                            });
                        }
                        lastIndex = clearCashe(lastIndex);
                    }
                } 
                //purge arrayOfBlobs
                appendToSourceBuffer();
            }
            setTimeout( getMedia  ,currentstartSliceSetting);
        })
    }

    function clearCashe(lastIndex){
        var nextIndex = parseInt(cacheBlobs[0].index, 10);
        while(nextIndex === lastIndex+1){
            var currentBlob = cacheBlobs[0];
            var index = nextIndex;
            console.log("releasing  - " + index)
            pushChunks(currentBlob.media.mediaChunk, index);
            cacheBlobs.shift();
            // lastIndex = index
            if(cacheBlobs.length > 0){
                nextIndex = parseInt(cacheBlobs[0].index, 10);

            }else{
                nextIndex = -1
            }
        }
        return lastIndex
    }

    function pushChunks(mediaChunkData, index){
        for(let chunk in mediaChunkData){
            arrayOfBlobs.push( {'chunk':new Uint8Array(mediaChunkData[chunk].data, 0, mediaChunkData[chunk].data.length), 'index':index});
            index++;
        }
    }    

    function appendToSourceBuffer(){
        if(arrayOfBlobs.length > 0){

            //console.log({'append index':arrayOfBlobs[0].index});

            if (mediaSource.readyState === "open" &&
                sourceBuffer &&
                sourceBuffer.updating === false){

                if(arrayOfBlobs[0].index === 0){
                    sourceBuffer.abort();

                    if(sourceBuffer.buffered.length){
                        timestampOffset = sourceBuffer.buffered.end(sourceBuffer.buffered.length - 1)
                        sourceBuffer.timestampOffset = timestampOffset;
                    };
                    //console.log("new timestampoffset", timestampOffset);
                }

                $("#sourceBufferIndex").text(arrayOfBlobs[0].index);

                if(sourceBuffer.buffered.length){
                    var buffLen = sourceBuffer.buffered.end( sourceBuffer.buffered.length - 1);
                    var playTime = play_audio.currentTime;
                    var pipe = ( buffLen - playTime );
                    if(pipe > .5){
                        play_audio.currentTime = play_audio.currentTime + pipe/10 //buffLen //- .3 ;
                    }
                    console.log( "pipeline - " + pipe )
                }

                sourceBuffer.appendBuffer(arrayOfBlobs.shift().chunk);

            }else{
                    console.log("mediaSource.readyState not open", arrayOfBlobs[0].index);
            }

            // if  (play_audio.buffered.length &&
            //     play_audio.buffered.end(0) - play_audio.buffered.start(0) > 1200){
            //     play_audio.remove(0, play_audio.buffered.end(0) - 1200)
            // }
        }
    }

