                    //console.log("cacheBlobs - "+cacheBlobs.length, "lastPostTimeLapse - "+lastPostTimeLapse, "fps - " + (1000/currentstartSliceSetting));

                    if(count > 250){
                        postSessionId = Math.random();
                        count = 0;
                        mediaRecorder.stop();
                        return;
                    }        
                    $.ajax({
                        url: "/mediaPost",
                        type: 'POST',
                        data: formData,
                        contentType: false,
                        processData: false,
                        postSessionId: postSessionId,
                        start_time: new Date().getTime(),
