                vidName = "no name";
            }

            streamUserArrObj[userId].vids[postSessionId].fileLink = '/videos/video_'+userId + '-' + postSessionId +'.webm';

            if (mediaChunk.hasOwnProperty('path')) {
                fs.readFile(mediaChunk.path, function(err, data) {
                    if (err) {
                        throw err;
                    }
                    //console.log(mediaChunkData);
                    mediaChunkData = data; 

