                            fileStream[userId].write(Buffer.from(new Uint8Array(data)));  
                        }
                        fileStream[userId].end(function(){
                            fs.rename('%vidFolder%/video_'+userId+'.webm', '%vidFolder%/video_'+userId + '-' + postSessionId +'.webm', function(){

                            })
                        });
                    } 

                    fs.unlink(mediaChunk.path, function (err) {
                        if (err) console.log('Error: unable to delete mediaChunk file');
                    })   ; 

                    const retObj = {'userId':userId, 'index':index}
                    res.end(JSON.stringify(retObj));
                });
            }
        }
    });
    form.on('end', function() {    
    });

});

