    if (ids !== '#'){
        const sid = req.query.sid;
        const uid = ids;
        if(streamUserArrObj.hasOwnProperty(uid)){

            const retObj = {};
            retObj[uid] = {};
            retObj[uid].status = streamUserArrObj.status;
            retObj[uid].vids = {};
            retObj[uid].vids[sid] = streamUserArrObj[uid].vids[sid]


            res.end(JSON.stringify({"userObj":retObj, "len":streamUserArrObj.length})) ;
         }else{
            res.end(JSON.stringify({"len":streamUserArrObj.length})) 
         }

    }else{
         var userArr = [];
         for(let x in streamUserArrObj){
            for(let y in streamUserArrObj[x].vids){
                 userArr.push(x+","+y)
            }

        }
        userArr.sort(function(a,b){ //sort by date stamp
            const aIds = a.split(",");
            const bIds = b.split(",");

            const dsA = new Date(streamUserArrObj[aIds[0]].vids[aIds[1]].ds);
            const dsB = new Date(streamUserArrObj[bIds[0]].vids[bIds[1]].ds);

            return dsA < dsB ? 1 : -1;
        })
        res.end(JSON.stringify(userArr)) ;
     }
});

