function doFileList(){
    $.get("/fileList",{id:"#"},function(list){
        currentFileListItem=null;


        $("#%playId% video").get(0).pause();
        $(".fileListItem").attr('class',"col-xsm-12 col-md-4 col-lg-2 fileListItem");
        $(".fileListItemImg").show();
        $("#%playId%").hide();
        $("#%playId%").detach().prependTo('body');

        currentFileListItem = null;


        var listHtml ="";  
        if($("#%listDivName%-fileList").length === 0){
             var listHtml = '<div id="%listDivName%-fileList" class="row fileList"></div>';
            $("#fileListDiv").html(listHtml);
        }        

        for(let row in list){
            var x = list[row].split(",")[0];
            var y = list[row].split(",")[1];

             $.get("/fileList",{id:x,sid:y},function(dataObj){

                var fileList = dataObj.userObj;
                for(let id in fileList){
                    if(fileList.hasOwnProperty(id) ){
                        for(let fl in fileList[id].vids){

                            if( $('#fileListItem_'+id+'-'+fl).length === 0){
                                listHtml = '<div id="fileListItem_'+id+'-'+fl+'" class="col-xsm-12 col-md-4 col-lg-2 fileListItem" data-fileId="'+fileList[id].vids[fl].fileLink+'">'; 
                                //listHtml = '<div id="fileListItem_'+id+'-'+fl+'" class="col fileListItem" data-fileId="'+fileList[id].vids[fl].fileLink+'">'; 
                                listHtml += '<div class="fileListItemImg"><img class="fileListItemThumb"></div>';
                                listHtml += '<div class="fileListItemName"></div>';
                                listHtml += '<div class="fileListItemDesc"></div>';
                                listHtml += '<div class="fileListItemDS"></div>';
                                listHtml += '</div>';

                                //Find insert point based on sort in /fileList service
                                $( ".fileListItem" ).each(function( index ) {
                                    const ids = $( this ).attr("id").split("-") ;
                                    const thisUser = ids[0].replace("fileListItem_","");
                                    const thisVid = ids[1];
                                    const thisIndex = list.indexOf(thisUser+","+thisVid);
                                    const newIndex = list.indexOf(id+","+fl);

                                    if(thisIndex > newIndex){
                                        $(listHtml).insertBefore($( ".fileListItem" )[index]);
                                        listHtml = "";
                                    }
                                });

                                $("#%listDivName%-fileList").append(listHtml);  

                                const vidName = fileList[id].vids[fl].vidName;
                                const vidDesc = fileList[id].vids[fl].vidDesc;
                                const ds = fileList[id].vids[fl].ds
                                const thumb = fileList[id].vids[fl].thumb

                                $("#fileListItem_"+id+"-"+fl).find(".fileListItemName").text(vidName.substring(0,20));
                                $("#fileListItem_"+id+"-"+fl).find(".fileListItemDesc").text(vidDesc.substring(0,50));
                                $("#fileListItem_"+id+"-"+fl).find(".fileListItemDS").text(new Date(ds).toLocaleDateString());
                                if(thumb){
                                    if(thumb.length > 50){
                                        $("#fileListItem_"+id+"-"+fl).find(".fileListItemThumb").attr("src", thumb);
                                    }else{
                                        $("#fileListItem_"+id+"-"+fl).find(".fileListItemThumb").attr("src", "%noThumbImage%");
                                    }
                                }else{
                                    $("#fileListItem_"+id+"-"+fl).find(".fileListItemThumb").attr("src", "%noThumbImage%");
                                }

                            }
                        }
                    }
                }

                $(play_video).click(function(evt){
                    evt.stopPropagation();
                })

                //$(".fileListItem").on('click', function(event){
                $(".fileListItem").click( function(event){
                    if(currentFileListItem !== event.target.id && ! $(this).has(".fileListItem-checkbox").length){
                        if(event.target.id.includes("fileListItem")){
                            const url = $(this).attr('data-fileId');

                            $("#%playId%").detach().prependTo('#'+event.target.id);
                            $(".fileListItemImg").show();
                            $(this).find(".fileListItemImg").hide();

                            $(".fileListItem").attr('class',"col-xsm-12 col-md-4 col-lg-2 fileListItem");
                            $(this).attr('class',"col-xsm-12 col-md-4 col-lg-2 fileListItem fileListItemSelected");
                            $("#%playId%").show( 0, function(){

                                $(".fileListItemSelected").attr('class',"col-xsm-12 offset-md-1 col-md-10 fileListItem fileListItemSelected");

                                setTimeout(
                                    function(){
                                        $([document.documentElement, document.body]).animate({
                                            scrollTop: $("#%playId%").offset().top - 63
                                        }
                                    ,2000)}
                                ,1000);

                            });
                            play_video.src = url;
                            $(play_video).attr('poster',$(this).find(".fileListItemThumb").attr("src"));

                            currentFileListItem = event.target.id;
                        }
                    }
                });

             });

        }

    })

}
