    var height = 150;
    $("#stop").hide()
    $("#start").show()

    const audioBitsPerSecond = 64000; 
    const videoBitsPerSecond = 650000; 
    const sampleSize = 16;
    const channelCount = 2;

    function startStream(){
        navigator.mediaDevices.getUserMedia({ 
            audio: {'sampleSize': sampleSize,'channelCount': channelCount}, 
            video: { width: 1280, height: 720 }
        }).then(handleSuccess)
    }

    startButton.addEventListener('click', function() {
        postSessionId = Math.random().toString().replace('.','');
        $("#stop").show();
        $("#start").hide();
        $("#stop").attr("disabled", true);
        setTimeout(function(){$("#stop").attr("disabled", false)},1000)

        shouldStop = false;
        stopped = false;
        startStream();
    })

    // const nameInput = document.getElementById("%id%-nameInput");
    // nameInput.addEventListener('input', saveName);
    // function saveName(e) {
    //     localStorage.setItem('nameInput', $("#%id%-nameInput").val()); 
    // }
    // $("#%id%-nameInput").val(localStorage.getItem('nameInput') || "")

    function stopStream(){
        shouldStop = true;
        rec_vid.pause();
    }

    stopButton.addEventListener('click', function() {
        stopStream();
        $.get("/streamPostStop",{"userId":userId});
    })

    var currentstartSliceSetting = 3000;
    var postSessionId = Math.random();

    // $.get("/streamPostStop",{"userId":userId});

    var mediaRecorder=null;
    var handleSuccess = function(stream) {

        const options = {mimeType: 'video/webm; codecs="opus,vp8"', //video/webm; codecs="opus,vp8
            "audioBitsPerSecond" : audioBitsPerSecond, 
            "videoBitsPerSecond" : videoBitsPerSecond
        };
        mediaRecorder = new MediaRecorder(stream, options);

        rec_vid.srcObject = stream;
        rec_vid.play();

        var count = 0
        var lastPostTimeLapse = 0;
        var lastStartTime = new Date().getTime();

        mediaRecorder.onstop = function(event) {
            if(! shouldStop){
                // startStream();
            }
        }
        mediaRecorder.addEventListener('dataavailable', function(e) {
            if (e.data.size === 0){

                    setTimeout(requestData, currentstartSliceSetting);
            }else{

                if(stopped === false){

                    var formData = new FormData();

                    formData.append('mediaChunk', e.data);

                    if(count === 0){
                        const canvas = document.createElement("canvas");
                        canvas.width = rec_vid.videoWidth/2;
                        canvas.height = rec_vid.videoHeight/2;
                        canvas.getContext('2d').drawImage(rec_vid, 0, 0, canvas.width, canvas.height);
                        const dataURL = canvas.toDataURL('image/jpeg', 0.8);

                        formData.append('thumb', dataURL);
                    }

                    formData.append('postSessionId', postSessionId);
                    formData.append('index', count.toString());
                    formData.append('userId', userId);
                    if(count === 0){
                        formData.append('vidName', $("#%id%-nameInput").val());
                        formData.append('vidDescription', $("#%id%-descInput").val());
                        formData.append('ds', new Date())

                    }

                    $("#dataavailableIndex").text(count);
                    count ++;
                    $.ajax({
                        url: "/mediaPost",
                        type: 'POST',
                        data: formData,
                        contentType: false,
                        processData: false,
                        postSessionId: postSessionId,
                        start_time: new Date().getTime(),
                        success: function (data) {
                            //console.log("returned postSessionId - "+ this.postSessionId);
                            if(postSessionId !== this.postSessionId){
                                //console.log("blocked postSessionId - "+ this.postSessionId);
                            }else{

                                lastPostTimeLapse = new Date().getTime() - this.start_time
                                $("#postTimeLaps").text(lastPostTimeLapse + " ms");
                                setTimeout(requestData,currentstartSliceSetting);

                                const dataObj = data;

                                if(userId !== dataObj.userId){
                                    userId = dataObj.userId;
                                    localStorage.setItem('userId', userId); 
                                }
                            }
                        }
                    });
                }

                if(shouldStop === true && stopped === false) {

                    $("#stop").hide();
                    $("#start").show();
                    $("#start").attr("disabled", true);
                    setTimeout(function(){$("#start").attr("disabled", false)},1000);
                    mediaRecorder.requestData();
                    mediaRecorder.stop();
                    stopped = true;
                    //%onStop%
                }
            }
        });

        function requestData(){
            if(mediaRecorder.state ==="inactive"){
                mediaRecorder.start();   //currentstartSliceSetting);
                setTimeout(requestData, currentstartSliceSetting);
            }else if(mediaRecorder.state ==="recording"){
                mediaRecorder.requestData()
            }else{
                setTimeout(requestData, currentstartSliceSetting);
            }
        }

        setTimeout(requestData, 200);

    };

