function scrollFunction() {
    var scrollTop = $(document).scrollTop();
    var anchors = $('body').find('.anchor');
    var docHeight = $(document).height() - $(window).height();
    var buttons = $('.navbar-right').find('li');

    for (var i = 0; i < anchors.length; i++) {

        if (scrollTop > $(anchors[i]).offset().top - 100 && scrollTop < $(anchors[i]).offset().top + $(anchors[i]).height() - 100) {
            $(buttons[i]).addClass('active');
        } else {
            $(buttons[i]).removeClass('active');
        }

        if (scrollTop > docHeight - 50) {
            buttons.removeClass('active');
            buttons.slice(-1).addClass('active')
        }
    }

//grow or shrink nav
    if (document.body.scrollTop > 80 || document.documentElement.scrollTop > 80) {
        $("#myNavbar").addClass("navbar-shrink");

    } else {
        $("#myNavbar").removeClass("navbar-shrink");
    }
}


