function %functionName%() {

    $("#%divToUpdate%").html("");
    $("#%statusDivId%-insert").html("Getting Tail Status");
    var currentRespText = '';
    var remainder = '';
    $.ajax({
        url: '%prefix%%serviceName%',
        processData: false,
        contentType: false,
        type: 'get',
        data: '',
        xhr: function () {

            var xhr = $.ajaxSettings.xhr();

            xhr.onprogress = function e() {
                $("#%statusDivId%-insert").html("Tailing...");
                var rText = xhr.responseText

                var rTextNew = rText.substr(currentRespText.length)
                currentRespText = rText;
                rTextNew = remainder + rTextNew.replace(/(?:\r\n|\r|\n)/g, '<br>');

                var newLines = rTextNew.split('<br>');

                //Anything past the last <br> is probably an incomplete row. Lets remove it and tack it on to beginning of next response.
                remainder = newLines[newLines.length - 1];
                newLines.pop();

                var headerHtml = '<div class = "flexbox logHeaderRow"><div class = "logDate">Time</div>';
                headerHtml += '<div class = "logMd">Method</div>';
                headerHtml += '<div class = "logProtocol">Proto</div>';
                headerHtml += '<div class = "logHost">Host</div>';
                headerHtml += '<div class = "logRad">Remote Address</div>';
                headerHtml += '<div class = "logPathname">Path</div>';
                headerHtml += '<div class = "logReferrer">Referrer</div></div>';
                $("#%headerDivToUpdate%").html(headerHtml);

                newLines.forEach(function(row){
                    //console.log(row); 
                    var rowObj = JSON.parse(row);
                    if(rowObj.hasOwnProperty("ping")){

                    }else{
                        var rowHtml = '<div class = "flexbox logRow">'
                        var nd = rowObj.date.split("_");
                        nd[1] = nd[1].replace(/-/g, ':');
                        var dStr = nd[0] + "T" + nd[1];

                        rowHtml += '<div class = "logDate">' + new Date(dStr).toISOString() + '</div>';
                        rowHtml += '<div class = "logMd">' + rowObj.md + '</div>';
                        rowHtml += '<div class = "logProtocol">' + rowObj.protocol + '</div>';
                        rowHtml += '<div class = "logHost">' + rowObj.host + '</div>';
                        rowHtml += '<div class = "logRad">' + rowObj.rad + '</div>';
                        rowHtml += '<div class = "logPathname">' + rowObj.pathname + '</div>';
                        if(rowObj.hasOwnProperty("referrer")){
                            rowHtml += '<div class = "logReferrer">' + rowObj.referrer + '</div>';
                        }

                        rowHtml += '</div>';

                        $("#%divToUpdate%").prepend(rowHtml);
                    }
                })
            };
            return xhr;
        }
    }).done(function (e) {
        $("#%statusDivId%-insert").html("Server stopped tailing");
        console.log("/getlog done?");

    }).fail(function (e) {

        $("#%statusDivId%-insert").html("Server stopped tailing");
        console.log("/getlog error");
        console.log(e)

    });
};
