3e619a82-e5b4-4834-a193-699df20be6d0.template:
router.get("/%serviceEndPointName%",function(req,res){
    var id = req.query.id;

    res.writeHead(200, {"Content-Type": "application/json"});
    var resJSON = [];
    if (id !== '#'){
        var rowdata = %jsonVar%[id];
        //console.log('gv:' + %jsonVar%[id].variables);
        rowdata.id = id;
        resJSON.push(rowdata);
    }else{
        for (var key in %jsonVar%) {
            if (%jsonVar%.hasOwnProperty(key)) {
                var rowdata = %jsonVar%[key];
                rowdata.id = key;
                resJSON.push(rowdata);
            }
        }
    }
    res.end(JSON.stringify(resJSON));
});

