9976def6-8a57-47db-85cb-7c646d7aa626.template:
var fs = require('fs');
const SystemsJSONContents = fs.readFileSync('%sourceJSON%');
const SystemsJSON = JSON.parse(SystemsJSONContents);
var newJSON= {};

function familyTreeNames(ft){
    var ftAr = ft.split('/');
    var ftNames =ft;
    ftAr.forEach(function(ans){
        if(ans !== '#'){
            ftNames = ftNames.replace(ans,SystemsJSON[ans].name)
        }
    })
    return(ftNames);
}

function addRow(key){
    newJSON[key] = {};
    newJSON[key].id = key;
    newJSON[key].name = SystemsJSON[key].name;
    newJSON[key].text = SystemsJSON[key].text;
    newJSON[key].description = SystemsJSON[key].description;
    newJSON[key].ft = SystemsJSON[key].ft;
    newJSON[key].parent = SystemsJSON[key].parent;
    newJSON[key].sort = SystemsJSON[key].sort;
    newJSON[key].type = SystemsJSON[key].type;
    newJSON[key].variables = SystemsJSON[key].variables;
    newJSON[key].ver = SystemsJSON[key].ver;

    if(SystemsJSON[key].type !== "system"){
        newJSON[key].enabled = SystemsJSON[key].enabled;
        newJSON[key].script = SystemsJSON[key].script;
        newJSON[key].template = SystemsJSON[key].template;
        newJSON[key].custTemplates = SystemsJSON[key].custTemplates;
        newJSON[key].resourceFiles = SystemsJSON[key].resourceFiles;
    }else{
        newJSON[key].icon = SystemsJSON[key].icon;
    }

    console.log('saved:' + familyTreeNames(newJSON[key].ft) + '/' + newJSON[key].name);

    if ( fs.existsSync( "%sourceUploads%" + "/" + key ) ) { //copy file resources if they exist
        if (!fs.existsSync("./uploads/" + key)) {
            fs.mkdirSync("./uploads/" + key)
        }
        files = fs.readdirSync("%sourceUploads%" + "/" + key);
        files.forEach(function (file) {
            if (!fs.lstatSync("%sourceUploads%" + "/" + key + '/' + file).isDirectory()) {
                var targetFile = "./uploads/" + key + '/' + file;
                var source = "%sourceUploads%" + "/" + key + '/' + file;
                fs.writeFileSync(targetFile, fs.readFileSync(source))
            }
        })
        console.log('file resources:' + files);

    }

}


for (var key in SystemsJSON) {
    if (SystemsJSON.hasOwnProperty(key)) {

        var famTree = SystemsJSON[key].ft.split('/');
        famTree.shift();

        if (famTree.length > 1) {
            if ((SystemsJSON[famTree[0]].name === 'Library') && (SystemsJSON[famTree[1]].name === 'public')) {
                addRow(key);
            }
        } else {
            if ((SystemsJSON[key].name === 'Library') && (SystemsJSON[key].ft = '#')) {
                addRow(key);
            }else if ((SystemsJSON[key].name === 'public') && (SystemsJSON[SystemsJSON[key].parent].name = 'Library') && (famTree.length === 1)) {
                addRow(key);
            }
        }
    }
}

fs.writeFile('%destJSON%', JSON.stringify(newJSON), function (err) {
    if (err) {
        console.log('There has been an error saving your json.');
        console.log(err.message);
        return;
    }
    console.log('json saved successfully.');
});

