       var rObj = data[0];
       $("#id").val(rObj.id);
       var fields='%fields%';
       fields.split(';').forEach(function(field){
		var fName=rObj[field];
		 var fieldObj = $("#" + field);
      		fieldObj.val(fName);
        })
    })
}

