ff5a3fbc-9c7c-45e6-9539-a32056ff710c.template:
function %functionName%(divHtmlToUpdate, questionField, answerField){
    var mydata={'id':'#'};
    $.ajax({
        type: 'get',
        data: mydata,
        dataType : "text",
        headers: {
            "content-type": "application/json"
        },
        url: '/%dataStoreGetService%',
        success : function(result) {

        var ResultsJson = JSON.parse(result);

        var html = '';
        ResultsJson.forEach(function(row){
        	var question = row.%questionField%;
        	question = question.replace(/</g, "&lt;").replace(/>/g, "&gt;");

        	var answer= row.%answerField%;
		answer = answer.replace(/</g, "&lt;").replace(/>/g, "&gt;");

		html += '<p class="%questionField%">'+%questionField%+'</p>';
                html += '<p class="%answerField%">'+%answerField%+'</p>';
             })
        $("#"+divHtmlToUpdate).html(html) ;

        },
        error : function(error) {
            console.log(error)
        }
    })
}

