							sz:"10/20/11",
							"children":[
							  {
								"id":"header",
								sz:"2/20/11"
							  },{
								"id":"form",
								sz:"8/20/13"
							  }
							]
						  }
						]
					  }
					]
				  }
				]
			  }
			]
		  }
		]
	  }
	]
  }
]

//build pareticles
    function buildParticles(jAr,wx, wy, bcrombParent ) {
       for (var i = 0; i < jAr.length; i++) {

            var bcromb = bcrombParent !== '' ? bcrombParent + '.' + i.toString():i.toString()

           //var numOfSibs =  jAr.length
           var size = jAr[i]['sz'] ? jAr[i]['sz'] : '';
            particle = new Particle(jAr[i]['id'], wx, wy, size, bcromb);
            particles.push(particle);

            if (jAr[i]['children'] && jAr[i]['children'].length) {
                buildParticles(jAr[i]['children'],wx, wy, bcromb)
            }
        }
    }


    function Particle(id, wx, wy, size, bcromb) {

        this.id = id;

        this.y = wy - (wy/2);
        this.wx = wx;
        this.wy = wy;
        this.size = size

        this.bcromb = bcromb;
        //this.sibCount = sibCount;

        this.speed =  ((Math.random() * 3) +2);

        //this.img = img
    }

    //img.onload = function () {
        buildParticles(JsonArr,wx, wy, '');
    //};

    var lastScroll=0;
    function renderFrame() {
        requestAnimationFrame(renderFrame);
        var currentScroll = $(window).scrollTop();

        var W = wx;
        var H = wy;
        ctx.clearRect(0, 0, W+5, H);

        particles.forEach(function (particle) {

            var lvl = particle.bcromb.split('.').length;

            var xp = (5+(particle.wx/particle.size.split('/')[1]) * (particle.size.split('/')[2]-1));
            var yp = 25 * lvl;
            var wid = ((particle.wx/particle.size.split('/')[1]) * particle.size.split('/')[0])-5;

            if (currentScroll > lastScroll || currentScroll < lastScroll){
                particle.y = particle.y - (currentScroll - lastScroll);
            }

            particle.speed = particle.speed - .4; //gravity sucks

            particle.speed = particle.speed * 0.98; //friction 
            particle.speed = particle.speed > 10 ? 10:particle.speed;
            particle.speed = particle.speed < -10 ? -10:particle.speed;

            particle.y += particle.speed;

            if(particle.y < yp){
                particle.y = yp;
                particle.speed = -(particle.speed + (Math.random()*(particle.speed  * .2) - (particle.speed  * .1)));
            }

            ctx.fillStyle = '#31312f';

            var rectHeight = 20;
            var rectWidth = wid;
            var rectX = xp;
            var rectY = particle.wy - particle.y;

            ctx.shadowBlur=10;
            ctx.shadowColor="black";
            ctx.fillRect( rectX  , rectY , wid, 20);

            ctx.font="14px Georgia";
            ctx.textAlign="center";
            ctx.textBaseline = "middle";

            ctx.fillStyle = "#fafaee";
            ctx.shadowBlur=0;
            ctx.fillText(particle.id,rectX+(rectWidth/2),rectY+(rectHeight/2));

        });
        lastScroll = currentScroll;
    }

    renderFrame();
})

