function startBtnClicked() {
    if($("#instanceID").val() === ""){
        $("#statusLine")[0].innerHTML = "<b>Enter an instance id</b>";
    }else{
        localStorage['instanceID'] = $("#instanceID").val();
        localStorage['forwardURL'] = $("#forwardURL").val();
        localStorage['forwardTrigger'] = $("#forwardTrigger").val();
        $("#runStartButton")[0].disabled = true;
        %functionName%();
    }
}


var instanceID = localStorage['instanceID'];
$("#instanceID").val(instanceID);
var forwardURL = localStorage['forwardURL']  ;
$("#forwardURL").val(forwardURL);

storedAccess = localStorage['access'];
storedAccessName = localStorage['accessName'];
if (storedAccess !== '' && typeof storedAccess !== 'undefined') {
    $('#localStoreAccessCb').prop('checked', true);
    $('#accessList').html(storedAccessName);
} else {
    $('#localStoreAccessCb').prop('checked', false);
    $('#accessList').html('');
}

if(instanceID){
    %functionName%()
}else{
    $("#runStartButton").show()
};

function %functionName%() {

    var currentRespText = "";
    var localAccess = "";
    var formData = new FormData();

    forwardURL = $("#forwardURL").val();

    formData.append('instanceID', $("#instanceID").val());

    if ($('#localStoreAccessCb').is(':checked')) {
        formData.append('storeLocalAccess', 'yes');
        formData.append('localStoredAccess', localStorage['access']);
        localStorage['forwardURL'] = forwardURL;
    } else {
        formData.append('storeLocalAccess', 'no');
        localStorage['access'] = '';
        localStorage['accessName'] = '';
        localStorage['forwardURL'] = '';
    }

    var files = $('#upload-access').get(0).files
    if (files.length === 1) {
        var file = files[0];
        formData.append('access', file, file.name);
        if ($('#localStoreAccessCb').is(':checked')) {
            localStorage['accessName'] = file.name;
        }
    }

    var x = 0;
    $.ajax({
        url: '/%serviceName%',
        processData: false,
        contentType: false,
        type: 'POST',
        data: formData,
        xhr: function () {

            $('#upload-key').val('');

            var xhr = $.ajaxSettings.xhr();

            xhr.onprogress = function e() {
                var rText = xhr.responseText

                var rTextNew = rText.substr(currentRespText.length)
                currentRespText = rText;

                rTextNew.split('\n').forEach(function (row) {

                    if (row.substr(0, 7) === 'access:') {
                        localAccess += row.substr(7) + '\n';
                        if ($('#localStoreAccessCb').is(':checked')) {
                            localStorage['access'] = localAccess
                        }
                    }
                    if (row.substr(0, 8) === 'message:') {
                        $("#statusLine")[0].innerHTML = row.substr(8);
                        console.log("message: "+row.substr(8));
                    }
                })
            };
            return xhr;
        }
    }).done(function (e) {
        if(forwardURL !== ""){
            $("#statusLine")[0].innerHTML += "<br>Waiting for service... <img src='/images/throbber.gif' height='11' width='11'> ";


            tryURL(x)


            // var img = document.body.appendChild(document.createElement("img"));
            // img.src = forwardURL;
            // img.onload = function()
            // {
            //     $("#statusLine")[0].innerHTML = "Redirecting...";
            //      window.location.href = forwardURL
            // };
            // img.onerror = function()
            // {
            //      $("#statusLine")[0].innerHTML = "Redirecting...";
            //      window.location.href = forwardURL
            // };

        }else{
            $("#runStartButton").show();
            $("#runStartButton")[0].disabled = false;
        }

    }).fail(function (e) {$("#statusLine")[0].innerHTML = "Error - See console";
        $("#statusLine")[0].innerHTML = "Error communicating with service: %serviceName% - See console";
        console.log("fail")
    });

    function tryURL(){
        var timeout = 1000;
        var controller = new AbortController();
        var signal = controller.signal;
        var options = { mode: 'no-cors', signal };
        setTimeout(() => { controller.abort() }, timeout);
        var resp = fetch(forwardURL, options)
        // .then(setTimeout(() => { controller.abort() }, timeout))
        .then(response => {
            $("#statusLine")[0].innerHTML = "Redirecting.";
             window.location.href = forwardURL
        })
        .catch(error => {
            x++;
             $("#statusLine")[0].innerHTML += ".";
             window.location.href = forwardURL
            if(x < 20) { 
                setTimeout(() => { tryURL() }, timeout)
            }else{
                $("#statusLine")[0].innerHTML = "Error("+x.toString()+") - Service not found";
                $("#runStartButton").show();
                $("#runStartButton")[0].disabled = false;
            }
        })
    }
}



