// Load the SDK for JavaScript
const AWS = require('aws-sdk');
const fs = require('fs');
const homedir = require('os').homedir();
const authorized_keys = fs.readFileSync("/home/ec2-user/.ssh/authorized_keys", "utf8").split("\n")[0];

var keyName="";

if(authorized_keys.toString().split(" ").length === 3){
		keyName = authorized_keys.toString().split(" ")[2].replace("\n","");
		console.log("Key being used: "+ keyName)
}else{
		console.log("Error: Keyname not found in /home/ec2-user/.ssh/authorized_keys. authorized_keys.split(' ').length !== 3 ?")
		process.exit()
}

// Load credentials and set region from JSON file
AWS.config.loadFromPath(homedir + '/accessConfig.json');

// Set the region
AWS.config.update({region: '%region%'});

// Create EC2 service object
var ec2 = new AWS.EC2({apiVersion: '2018-03-20'});

//Get the new Security Group Id
var GroupIdOb = JSON.parse(fs.readFileSync('secGroupID.json'));
var GroupId = GroupIdOb.GroupId;
console.log("GroupId being used: " + GroupId );
// AMI is Red Hat Enterprise Linux 7.4 (HVM)
var instanceParams1 = {
    ImageId: 'ami-223f945a',
    InstanceType: '%instanceType%',
    KeyName: keyName,
    MinCount: 1,
    MaxCount: 1,
    Placement: {
        AvailabilityZone: "%availabilityZone%"
    },
    SecurityGroupIds: [GroupId]
};

var instList = []; //Store instance IDs
createInstances(); //Create all instances async

function createInstances() {
    try {
        //create multiple

        var instancePromise1 = new AWS.EC2({apiVersion: '2016-11-15'}).runInstances(instanceParams1).promise();
        createInstancesTags( instancePromise1);


    } catch(e) {

    }
}

//function to tag a given instance. Provide a tag name and a promise for a new instance
function createInstancesTags(instancePromise ){

    instancePromise.then(
        function(data) {
            data.Instances.forEach(function(instance){

                tagName = "ezStack Demo Instance";
                var instanceId = instance.InstanceId;
                console.log("Created instance", instanceId);

                // Add tags to the instance
                tagParams = {
                    Resources: [instanceId], Tags: [
                        {
                            Key: 'Name',
                            Value: tagName
                        }
                    ]
                };

                //create a tag promise
                var tagPromise = new AWS.EC2({apiVersion: '2016-11-15'}).createTags(tagParams).promise();

                //then describe instance, get inst id and save instList.json
                tagPromise.then(
                    function (data) {
                        console.log("Instance tagged: " + instanceId);

                        var DIparams = {
                            Filters: [
                                {
                                    Name: 'instance-id',
                                    Values: [instanceId]
                                }
                            ]
                        };

                        ec2.describeInstances(DIparams, function (err, data) {
                            if (err) return console.error(err.message);
                            instList.push({'inst': instanceId});

                            console.log({'inst': instanceId});
                            saveInstList();

                            function saveInstList() {
                                fs.writeFile('instList.json', JSON.stringify(instList), function (err) {
                                    if (err) {
                                        console.log('There has been an error saving Instance json');
                                        console.log(err.message);
                                    }
                                })
                            }
                        });

                    }).catch(
                    function (err) {
                        console.error(err, err.stack);
                    });


            })

        }).catch(
        function(err) {
            console.error(err, err.stack);
        });
}

50394b77-31e2-4bc1-b5ff-9de6d7cf6463.template:
const fs = require('fs');
const AWS = require('aws-sdk');
const homedir = require('os').homedir();

// Load credentials and set region from JSON file
AWS.config.loadFromPath(homedir + '/accessConfig.json');

// Set the region
AWS.config.update({region: 'us-west-2'});

// Create EC2 service object
var ec2 = new AWS.EC2({apiVersion: '2018-03-20'});

//Load list of instances
var instList = JSON.parse(fs.readFileSync('instList.json'));


//Create a new array to store instanceId and IP as json
var newJS = [];

//Loop through each inst and call getIP(inst)
instList.forEach(function(row){
    var ipAddy = getIP(row.inst);
});

//function to return ip fpr a given instanceId
function getIP(instanceId){
    console.log("Get IP Addy for: ", instanceId );
    var params = {
        Filters: [
            {
                Name: 'instance-id',
                Values: [instanceId]
            }
        ]
    };

    //use ec2.describeInstances to get IP address
    ec2.describeInstances(params, function (err, data) {
        if (err){
            console.error(err.message);
            return '';
        }else{
            //cb to push instanceId and IP json
            var publicIp = data.Reservations[0].Instances[0].PublicIpAddress;

            newJS.push({inst:instanceId, ip:publicIp});
            console.log("IP Addsress for " + instanceId, publicIp);
	    console.log("var:host:"+publicIp);
            fs.writeFile('ipList.json', JSON.stringify(newJS), function (err) {
                if (err) {
                    console.log('There has been an error saving IP List json');
                    console.log(err.message);
                }
            });
            return publicIp;
        }
    });
}

