const fs = require('fs');
const AWS = require('aws-sdk');
const homedir = require('os').homedir();

// Load credentials and set region from JSON file
AWS.config.loadFromPath(homedir + '/accessConfig.json');

// Set the region
AWS.config.update({region: 'us-west-2'});

// Create EC2 service object
var ec2 = new AWS.EC2({apiVersion: '2018-03-20'});

//Load list of instances
var instList = JSON.parse(fs.readFileSync('instList.json'));


//Create a new array to store instanceId and IP as json
var newJS = [];

//Loop through each inst and call getIP(inst)
instList.forEach(function(row){
    var ipAddy = getIP(row.inst);
});

//function to return ip fpr a given instanceId
function getIP(instanceId){
    console.log("Get IP Addy for: ", instanceId );
    var params = {
        Filters: [
            {
                Name: 'instance-id',
                Values: [instanceId]
            }
        ]
    };

    //use ec2.describeInstances to get IP address
    ec2.describeInstances(params, function (err, data) {
        if (err){
            console.error(err.message);
            return '';
        }else{
            //cb to push instanceId and IP json
            var publicIp = data.Reservations[0].Instances[0].PublicIpAddress;

            newJS.push({inst:instanceId, ip:publicIp});
            console.log("IP Addsress for " + instanceId, publicIp);

	        console.log("var:systemVar:host="+publicIp);
            fs.writeFile('ipList.json', JSON.stringify(newJS), function (err) {
                if (err) {
                    console.log('There has been an error saving IP List json');
                    console.log(err.message);
                }
            });
            return publicIp;
        }
    });
}

