    //console.log("here: ");
    var reqJSON= req.body;
    var id =reqJSON.id
    //console.log("del: "+id);
    delete %jsonVar%[id];

    %saveServiceEndPointName%();
    res.end('');
});

router.get("/%serviceEndPointName%",function(req,res){
    var id = req.query.id;

    res.writeHead(200, {"Content-Type": "application/json"});
    var resJSON = [];
    if (id !== '#'){
        var rowdata = %jsonVar%[id];
        //console.log('gv:' + %jsonVar%[id].variables);
        rowdata.id = id;
        resJSON.push(rowdata);
    }else{
        for (var key in %jsonVar%) {
            if (%jsonVar%.hasOwnProperty(key)) {
                var rowdata = %jsonVar%[key];
                rowdata.id = key;
                resJSON.push(rowdata);
            }
        }
    }
    res.end(JSON.stringify(resJSON));
});

router.post("/%serviceEndPointName%",function(req,res){

    var reqJSON= req.body;
    var rowId = "";

    if (typeof reqJSON.id === 'undefined' || reqJSON.id === ''){
        rowId = generateUUID()
    }else{
        rowId =  reqJSON.id
    }
    %jsonVar%[rowId] = req.body;

    %serviceEndPointName%();

    var returnRow = %jsonVar%[rowId];
    returnRow.id=rowId;

   res.end(JSON.stringify(returnRow));
});

function %serviceEndPointName%(){
    fs.writeFile('%saveFileName%', JSON.stringify(%jsonVar%), function (err) {
        if (err) {
            console.log('There has been an error saving your json: ' + '%jsonVar%' + " -> " + "%saveFileName%" );
            console.log(err.message);
            return;
        }
        console.log('json saved successfully: ' + '%jsonVar%' + " -> " + "%saveFileName%")
    })
}

