insStr=$1;
insRep=$2;
targetFile=$3;
echo replacing: $insStr with $insRep; 
echo found in: $(grep -c "$insStr" $targetFile) lines;
sed -i -e 's|'"$insStr"'|'"$insRep"'|g' $targetFile

