function createInstances() {
    try {
        //create multiple

        var instancePromise1 = new AWS.EC2({apiVersion: '2016-11-15'}).runInstances(instanceParams1).promise();
        createInstancesTags( instancePromise1);


    } catch(e) {

    }
}

//function to tag a given instance. Provide a tag name and a promise for a new instance
function createInstancesTags(instancePromise ){

    instancePromise.then(
        function(data) {
            data.Instances.forEach(function(instance){

                tagName = "ezStack Demo Instance";
                var instanceId = instance.InstanceId;
                console.log("Created instance", instanceId);

                // Add tags to the instance
                tagParams = {
                    Resources: [instanceId], Tags: [
                        {
                            Key: 'Name',
                            Value: tagName
                        }
                    ]
                };

                //create a tag promise
                var tagPromise = new AWS.EC2({apiVersion: '2016-11-15'}).createTags(tagParams).promise();

                //then describe instance, get inst id and save instList.json
                tagPromise.then(
                    function (data) {
                        console.log("Instance tagged: " + instanceId);

                        var DIparams = {
                            Filters: [
                                {
                                    Name: 'instance-id',
                                    Values: [instanceId]
                                }
                            ]
                        };

                        ec2.describeInstances(DIparams, function (err, data) {
                            if (err) return console.error(err.message);
                            instList.push({'inst': instanceId});

                            console.log({'inst': instanceId});
                            saveInstList();

                            function saveInstList() {
                                fs.writeFile('instList.json', JSON.stringify(instList), function (err) {
                                    if (err) {
                                        console.log('There has been an error saving Instance json');
                                        console.log(err.message);
                                    }
                                })
                            }
                        });

                    }).catch(
                    function (err) {
                        console.error(err, err.stack);
                    });


            })

        }).catch(
        function(err) {
            console.error(err, err.stack);
        });
}
